mapper.select:
select 

from cxyproductrspecattr
where productid = #{productid,jdbcType=INTEGER}
  and specattrid = #{specattrid,jdbcType=INTEGER}

mapper.delete:
delete from cxyproductrspecattr
where productid = #{productid,jdbcType=INTEGER}
  and specattrid = #{specattrid,jdbcType=INTEGER}

mapper.update:
