    deleted = #{deleted,jdbcType=INTEGER},


    DataChange_LastTime = #{datachangeLasttime,jdbcType=TIMESTAMP},


where productid = #{productid,jdbcType=INTEGER}
  and specattrid = #{specattrid,jdbcType=INTEGER}

